import React, { useEffect, useState } from 'react';

const App = () => {
  const [location, setLocation] = useState('')
  const [showing, setShowing] = useState('')
  console.log(showing)
  console.log(location)
  const keyFinder = (e) => {
    let words = e.target.value
    setLocation(words)
  }

  const showingLocations = () => {

  }

  useEffect(() => {
    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': 'ee7f409780mshf5b89eddc2790f1p1813c6jsn67caa6f07a7d',
        'X-RapidAPI-Host': 'weatherbit-v1-mashape.p.rapidapi.com'
      }
    };

    fetch(`https://weatherbit-v1-mashape.p.rapidapi.com/${location}?lon=38.5&lat=-78.5`, options)
      .then(response => response.json())
      .then(response => console.log(response))
      .then(response => setShowing(response))

      .catch(err => console.error(err));
  }, [location])

  return (
    <div>
      <div>
        <h3>Fully Weather App</h3>
      </div>

      <div>

        <input type='text' placeholder='Enter location' required onChange={keyFinder} />



      </div>
    </div>
  );
}

export default App;
