import React, { useState } from 'react';
import './App.css'
const API_KEY = {
  key: '5025682a09668f06c331b6ae4408a453'
}

const App = () => {
  const [citiesByWord, setCitiesByWord] = useState('')
  const [showingOnScreen, setShowingOnScreen] = useState({})

  const location = () => {
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${citiesByWord}&appid=${API_KEY.key}`)
      .then(resp => resp.json())
      .then(resp => setShowingOnScreen(resp))
      .then(resp => console.log(resp))
      .then(error => console.log(error))
  }
  const citiesFinderByText = (e) => {
    setCitiesByWord(e.target.value)
  }
  const searching = () => {
    location(citiesByWord)
  }


  return (
    <div>
      <div>
        <h3>Weather App</h3>
      </div>
      <div>
        <h6>Find your favourite cities weather here</h6>
      </div>
      <div>
        <input type='text' required value={citiesByWord} onChange={citiesFinderByText} />
        <button onClick={searching}>Click here to Search</button>
      </div>

      <div>
        {showingOnScreen?.name ? <h3>{showingOnScreen?.name}</h3> : <h3>City Name</h3>}
        {showingOnScreen?.main?.temp ? <h3>{((showingOnScreen?.main?.temp) - 273.15).toFixed(2)}'C</h3> : `0'C`}
      </div>
    </div>
  );
}

export default App;
